  SELECT    Estudo.intEstudoId        AS EstudoId,    Estudo.strDescricao       AS EstudoDescricao,    Estudo.strProprietario    AS EstudoProprietario,    Estudo.tmsModificacao     AS EstudoModificacao,    Estudo.intTipoEstudoId    AS TipoEstudoId,	Estudo.strJustificativa   AS Justificativa  FROM    Estudo  WHERE    (( [p_EstudoId] = -1) OR (Estudo.intEstudoId = [p_EstudoId] ));